import {
    saveUser,
    logOutUser
} from '../../../utils';

function getUrl(){
    let URL = 'http://localhost:3000/dataLogin.json';
    return URL;
}
/**
*
* @returns {Function}
*/
export default function login(body) {
    console.log(body,"body")
    const URL = getUrl();
    return function (dispatch) {
        dispatch(
            request()
        );
        fetch(URL, body)
        .then((json) =>{
            if(!json.error){
                if(saveUser(json.data)) {
                    console.log(body,Promise.PromiseValue.username)
                    dispatch(
                        received(json)
                    )
                }
                else {
                    dispatch(failed({error: true, statusCode: "400", message: "Something went wrong"}));
                }
            }
            else {
                dispatch(failed(json));
            }
        }).catch(error => {
            dispatch(failed(error));
        });
    };
}

/**
*
* @returns {{type}}
*/
function request() {
    return {
        type: 'LOGIN_PENDING',
    };
}

/**
*
* @param response
* @returns {{type, payload: {json: *}}}
*/
function received(response) {
    return {
        type: 'LOGIN',
        payload: {
            response
        }
    };
}

/**
*
* @param error
* @returns {{type, error: *}}
*/
function failed(error) {
    return {
        type: 'LOGIN_FAILED',
        error
    };
}
