function getUrl() {
    return END_POINTS.AUTH.LOGIN;
}

/**
*
* @returns {Function}
*/
export default function login(body) {
    const url = getUrl();
    return function (dispatch) {
        dispatch(
            request()
        );
        callApi(url, body).then(json =>{
            if(!json.error){
                if(saveUser(json.data)) {
                    dispatch(
                        received(json)
                    )
                }
                else {
                    dispatch(failed({error: true, statusCode: "400", message: "Something went wrong"}));
                }
            }
            else {
                dispatch(failed(json));
            }
        }).catch(error => {
            dispatch(failed(error));
        });
    };
}

/**
*
* @returns {{type}}
*/
function request() {
    return {
        type: 'LOGIN_PENDING',
    };
}

/**
*
* @param response
* @returns {{type, payload: {json: *}}}
*/
function received(response) {
    return {
        type: 'LOGIN',
        payload: {
            response
        }
    };
}

/**
*
* @param error
* @returns {{type, error: *}}
*/
function failed(error) {
    return {
        type: 'LOGIN_FAILED',
        error
    };
}


export const logOut = () => {
    logOutUser();
    return {
        type: 'LOGOUT'
    };
};
