function getUrl(){
    let URL = 'http://localhost:3000/dataDashboard.json';
    return URL;
}
/**
*
* @returns {Function}
*/
export default function login(body) {
    const URL = getUrl();
    return function (dispatch) {
        dispatch(
            request()
        );
        fetch(URL, body).then(json =>{
            if(!json.error){
                    dispatch(
                        received(json)
                    )

            }
            else {
                dispatch(failed(json));
            }
        }).catch(error => {
            dispatch(failed(error));
        });
    };
}

/**
*
* @returns {{type}}
*/
function request() {
    return {
        type: 'LOGIN_PENDING',
    };
}

/**
*
* @param response
* @returns {{type, payload: {json: *}}}
*/
function received(response) {
    return {
        type: 'LOGIN',
        payload: {
            response
        }
    };
}

/**
*
* @param error
* @returns {{type, error: *}}
*/
function failed(error) {
    return {
        type: 'LOGIN_FAILED',
        error
    };
}
