const INITIAL_STATE ={
    userDashboard:{
        data:[],
        pending:null,
        failed:null
    }
}

export default function dashboardReducer(state=INITIAL_STATE,action={}){
    switch(action.type){
        case 'GET_ALL_DASHBOARD_USERS_PENDING':
        return {
            ...state,
            userDashboard:{
                ...state.userDashboard,
                failed: false,
                pending: true,
                error: ''
            }

        };
        case 'GET_ALL_DASHBOARD_USERS_FAILED':
        return {
            ...state,
            userDashboard:{
                ...state.userDashboard,
                failed:true,
                pending:false,
                error:''
            }
        };
        case 'GET_ALL_DASHBOARD_INFO':
        return {
            ...state,
            userDashboard:{
                ...state.userDashboard,
                data:action.payload.response,
                failed: false,
                pending: false,
                error: ''
            }

        };
        default :
        return state;
    }

}
