import React, {Component} from "react";
import {
    login
} from './actions';
import {connect} from "react-redux";

function mapStateToProps (state) {
    return {
        user: state.login
    };
}

function mapDispatchToProps (dispatch) {
    return {
        login: (body) => {
            dispatch(login(body));
        }
    };
}

class Login extends Component {
    constructor() {
        super();
    }
    componentWillMount(){
        if(isUserLoggedIn()){
            this.props.history.push('/users');
        }
    }

    componentWillReceiveProps(nextProps){
        const {user:{login:{pending, failed, error}}} = nextProps;
        const {user:{login:{pending: oldPending}}} = this.props;
        if(!pending && pending !== oldPending){
            if(failed){
                // let myColor = { background: '#df382c', text: "#FFFFFF" };
                // notify.show(nextProps.user.login.error, "custom", 6000, myColor);
            }else {
                this.props.history.push('/users');
            }
        }
    }

    submit = (e) => {
        e.preventDefault();
        let formData = new FormData(e.target)
        let data = {};
        for (var [key, value] of formData.entries()) {
            data[key] = value;
        }
        console.log(data);
        this.props.login(data);
    }

    render() {

        return (
            <div className="container">
                <div className="container">
                <div style={{marginTop: "4rem"}} className="row">
                    <div className="col s8 offset-s2">
                        <form noValidate onSubmit={this.onSubmit}>
                            <div className="input-field col s12">
                                <input
                                    id="email"
                                    type="email"
                                    onChange={this.onChange}
                                    value={this.state.email}
                                />
                                <label htmlFor="email">Email</label>
                                <span className="red-text">
                </span>
                            </div>
                            <div className="input-field col s12">
                                <input
                                    id="password"
                                    type="password"
                                    onChange={this.onChange}
                                    value={this.state.password}
                                />
                                <label htmlFor="password">Password</label>
                                <span className="red-text">

                </span>
                            </div>
                            <div className="col s12" style={{paddingLeft: "11.250px"}}>
                                <button
                                    style={{
                                        width: "150px",
                                        borderRadius: "3px",
                                        letterSpacing: "1.5px",
                                        marginTop: "1rem"
                                    }}
                                    type="submit"
                                    className="btn btn-large waves-effect waves-light hoverable blue accent-3">
                                    Login
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
            </div>
        );
    }
}
